INSERT INTO answer (uuid, status, content, question_id, created_at, updated_at) VALUES ('5ce0e6bf-79b8-48fa-9191-fba7a8b45ecc', 'ACTIVE', 'Đối với khoản vay thế chấp, Ngay khi cung cấp hồ sơ cho MSB, Khách hàng cần chuẩn bị phí định giá TSBĐ để MSB xác định số tiền cho vay dựa trên giá trị TSBĐ. Khi đã được phê duyệt khoản vay, KH cần chuẩn bị phí công chứng hợp đồng bảo đảm và phí đăng ký giao dịch bảo đảm tiền vay để hoàn thiện thủ tục thế chấp cho Ngân hàng trước khi giải ngân.', '46f1f888-7e60-4809-86bf-9eb41c9de58e', DEFAULT, DEFAULT);
