INSERT INTO answer (uuid, status, content, question_id, created_at, updated_at) VALUES ('fa45ac4e-9c4d-487e-ac4b-1c8cddc1e7c9', 'ACTIVE', 'Có, Khách hàng có thể trả nợ 1 phần hoặc toàn bộ nợ gốc trước hạn tùy vào tình hình tài chính. Phí trả nợ trước hạn căn cứ vào số tiền gốc trả trước hạn, hầu hết các gói lãi suất của MSB dành cho bạn đều miễn phí trả nợ trước hạn kể từ năm thứ 5 trở đi.', '190b5457-9b6b-4b1c-8dbf-db025e7d1f40', DEFAULT, DEFAULT);
INSERT INTO answer (uuid, status, content, question_id, created_at, updated_at) VALUES ('c7e4103f-feb5-4695-8849-a34e525c3731', 'ACTIVE', 'Có. MSB Tài trợ vốn phục vụ tiêu dùng, sinh hoạt bao gồm cả mục đích thanh toán học phí/ du lịch/ Khám chữa bệnh....cho Khách hàng, bố mẹ, con cái.', '897366b1-9255-4193-9d8c-6f3a83752839', DEFAULT, DEFAULT);
INSERT INTO answer (uuid, status, content, question_id, created_at, updated_at) VALUES ('12406dc6-0b52-4764-8bb3-caafb98dc66a', 'ACTIVE', 'Thời gian vay vốn đối với mục đích tiêu dùng có thế chấp tài sản tại MSB tối thiểu là 03 tháng, tối đa lên tới 10 năm.', '8c7c8fd4-829e-44b4-bfd8-d3a6207d4303', DEFAULT, DEFAULT);
INSERT INTO answer (uuid, status, content, question_id, created_at, updated_at) VALUES ('29c43b9e-e462-4c34-8742-fcb80de50ccf', 'ACTIVE', 'Có. MSB tài trợ mục đích vay vốn xây nhà để cho thuê lên tới 10 tỷ đồng, thời gian vay tối đa lên tới 15 năm.', 'b8a32973-db64-4d16-bfd8-96381e05377a', DEFAULT, DEFAULT);
