INSERT INTO answer (uuid, status, content, question_id, created_at, updated_at) VALUES ('f493cd5b-37ea-4ee9-9d07-0953275af174', 'ACTIVE', 'Có. Khách hàng có thể sử dụng Bất động sản đang giao dịch mua bán làm tài sản thế chấp cho khoản vay mua chính tài sản này (Áp dụng với Bất động sản đã có Giấy chứng nhận và phù hợp với quy định của Ngân hàng)', 'd6a9574e-9402-44b6-86f5-59815ec11cc7', DEFAULT, DEFAULT);
